  @@availability_time_options = []

  def availability_image(user)
    user.available? ? "play-btn-green.png" : "play-btn-gray.png"
  end

  def time_options_for_select
    return @@availability_time_options unless @@availability_time_options.blank?

    minutes = 15 * 60
    @@availability_time_options = Tod::Shift.new(Tod::TimeOfDay.new(0), Tod::TimeOfDay.new(23, 59)).range.step(minutes).to_a.map { |seconds|
      tod = Tod::TimeOfDay.new(0) + seconds
      [
        tod.to_s(:short).upcase,
        tod.strftime("%H:%M:%S")
      ]
    }
  end

  def profile_show_meta_description(user)
    [
      user.name,
      user.description,
      user.specialties,
      user.product_knowledge,
      users_rate(user),
      user.business_website,
      user.reviews.map { |review| "#{review.reviewer_name}: #{review.rating}, #{review.comment}" }.join(", "),
      user.address,
      user.phone_number,
      user.video_url,
      user.license_informations.map(&:category).join(", "),
      user.facebook_url,
      user.twitter_url,
      user.instagram_url,
      user.youtube_url,
      user.linkedin_url,
      user.google_my_business,
      user.picture.url(:small_thumb)
    ].join(", ")
  end

  def user_profile_show_meta_description(user)
    category = user&.categories&.first&.name&.downcase
    if category.present?
      if user.pro?
        PRO_DESCRIPTION[category]&.gsub('pro_first_name', user.first_name)
      else
        description = BUSINESS_DESCRIPTION[category]&.gsub('business_name', user.name.titleize)
        description&.gsub('(City/State)', [user.city, user.state].join(', '))&.gsub('(City)', [user.city].join(', '))
      end
    else
      profile_show_meta_description(user)
    end
  end

  def user_profile_Heading_Tags(user, h_tag)
    category = user&.categories&.first&.name&.downcase
    if category.present?
      if user.pro?
        case h_tag.downcase
        when "h1"
          PRO_H1_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h2"
          PRO_H2_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h3"
          PRO_H3_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h4"
          PRO_H4_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h5"
          PRO_H5_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h6"
          PRO_H6_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "map"
          PRO_MAP_TAG[category]&.gsub('pro_first_name', user.first_name)
        when "h3_content"
          PRO_H3_TAG_CONTENT[category]&.gsub('pro_first_name', user.first_name)
        when "h5_content"
          PRO_H5_TAG_CONTENT[category]&.gsub('pro_first_name', user.first_name)
        when "h6_content"
          PRO_H6_TAG_CONTENT[category]&.gsub('pro_first_name', user.first_name)
        else
          ""
        end
      else
        case h_tag.downcase
        when "h1"
          tag = BUSINESS_H1_TAG[category]
        when "h2"
          tag = BUSINESS_H2_TAG[category]
        when "h3"
          tag = BUSINESS_H3_TAG[category]
        when "h4"
          tag = BUSINESS_H4_TAG[category]
        when "h5"
          tag = BUSINESS_H5_TAG[category]
        when "h6"
          tag = BUSINESS_H6_TAG[category]
        when "map"
          tag = BUSINESS_MAP_TAG[category]
        when "h3_content"
          tag = BUSINESS_H3_TAG_CONTENT[category]
        when "h5_content"
          tag = BUSINESS_H5_TAG_CONTENT[category]
        when "h6_content"
          tag = BUSINESS_H6_TAG_CONTENT[category]
        else
          ""
        end
        tag&.gsub('business_name',user.name.titleize)&.gsub('(City/State)',"#{user.city} #{user.state}")&.gsub('(City)',"#{user.city}")
      end
    else
      ""
    end
  end

